class StrictEqualityExtension {
  getInfo() {
	return {
		id: 'Alert',
		name: '警告',
		color1: '#fcb103',
        color2: '#db9a37',
        color3: '#db8937',
		blockIconURI: "data:image/svg+xml;base64,",
		blocks: [
        {
          opcode: 'Alert1',
          blockType: Scratch.BlockType.COMMAND,
          text: '警告 [a]',
          arguments: {
            a: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: '这是个警告窗'
            },
          }
        }
,
		{
          opcode: 'Alert2',
          blockType: Scratch.BlockType.BOOLEAN,
          text: '询问 [a]',
          arguments: {
            a: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: '询问'
            },
          }
        }
,
		{
          opcode: 'Alert3',
          blockType: Scratch.BlockType.REPORTER,
          text: '询问 [a] 并获取输入',
          arguments: {
            a: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: '询问'
            },
          }
        }
      ]
    };
  }
  Alert1(args) {
    alert(args.a);
  }

  Alert2(args) {
	return window.confirm(args.a)
  }

  Alert3(args) {
	return prompt(args.a)
  }
}
Scratch.extensions.register(new StrictEqualityExtension());
